// var (function-scoped, avoid in modern JS)
var name = 'John';

// let (block-scoped, preferred)
let age = 25;

// const (block-scoped, immutable reference)
const PI = 3.14159;

// Reassignment
age = 26;           // OK
PI = 3.14;          // ERROR - const cannot be reassigned

// Hoisting
console.log(x);     // undefined (hoisted but not initialized)
var x = 5;

// if-else
const age = 18;
if (age >= 18) {
    console.log('Adult');
} else {
    console.log('Minor');
}

// switch
const day = 3;
switch (day) {
    case 1:
        console.log('Monday');
        break;
    case 3:
        console.log('Wednesday');
        break;
    default:
        console.log('Other day');
}

// Ternary operator
const status = age >= 18 ? 'Adult' : 'Minor';

// for loop
for (let i = 0; i < 5; i++) {
    console.log(i);  // 0 1 2 3 4
}

// while loop
let count = 0;
while (count < 5) {
    console.log(count);
    count++;
}

// do-while loop
let x = 0;
do {
    console.log(x);
    x++;
} while (x < 5);

// for-of loop (iterate values)
const arr = [10, 20, 30];
for (const value of arr) {
    console.log(value);
}

// for-in loop (iterate keys)
const obj = {a: 1, b: 2};
for (const key in obj) {
    console.log(key, obj[key]);
}

// Function declaration
function add(a, b) {
    return a + b;
}

// Function expression
const multiply = function(a, b) {
    return a * b;
};

// Arrow function (ES6+)
const subtract = (a, b) => a - b;

// Arrow function with multiple statements
const divide = (a, b) => {
    if (b === 0) return 'Cannot divide by zero';
    return a / b;
};

// Default parameters
const greet = (name = 'Guest') => `Hello, ${name}`;

// Rest parameters
const sum = (...numbers) => numbers.reduce((a, b) => a + b, 0);
sum(1, 2, 3, 4);  // 10

// Object literal
const person = {
    name: 'Alice',
    age: 30,
    email: 'alice@example.com',
    greet() {
        return `Hello, I'm ${this.name}`;
    }
};

// Accessing properties
console.log(person.name);           // 'Alice'
console.log(person['age']);         // 30

// Modifying properties
person.age = 31;
person.city = 'NYC';                // Add new property

// Destructuring
const {name, age} = person;
console.log(name);                  // 'Alice'

// Spread operator
const newPerson = {...person, age: 25};

const arr = [1, 2, 3, 4, 5];

// map - transform elements
const squared = arr.map(x => x * x);  // [1, 4, 9, 16, 25]

// filter - keep matching elements
const evens = arr.filter(x => x % 2 === 0);  // [2, 4]

// reduce - aggregate to single value
const sum = arr.reduce((acc, x) => acc + x, 0);  // 15

// forEach - iterate
arr.forEach(x => console.log(x));

// find - get first match
const first = arr.find(x => x > 3);  // 4

// includes - check existence
console.log(arr.includes(3));  // true

// push/pop/shift/unshift
arr.push(6);                   // Add to end
arr.pop();                     // Remove from end
arr.unshift(0);                // Add to start
arr.shift();                   // Remove from start

// Callback (older approach)
function fetchData(callback) {
    setTimeout(() => {
        callback('Data loaded');
    }, 1000);
}
fetchData(data => console.log(data));

// Promise
const promise = new Promise((resolve, reject) => {
    setTimeout(() => resolve('Success'), 1000);
});
promise.then(data => console.log(data));

// Async/Await (modern, cleaner)
async function loadData() {
    try {
        const response = await fetch('/api/data');
        const data = await response.json();
        console.log(data);
    } catch (error) {
        console.error('Error:', error);
    }
}

// Promise.all - wait for multiple
const results = await Promise.all([
    fetch('/api/users'),
    fetch('/api/posts')
]);

// Template literals (backticks)
const name = 'World';
const greeting = `Hello, ${name}!`;
const multiline = `Line 1
Line 2
Line 3`;

// Array destructuring
const [first, second, ...rest] = [1, 2, 3, 4, 5];
console.log(first);   // 1
console.log(rest);    // [3, 4, 5]

// Object destructuring
const {name: personName, age} = {name: 'Alice', age: 30};

// Rename in destructuring
const {name: n} = {name: 'Bob'};

// Default values
const {city = 'NYC'} = {};  // city = 'NYC'

// Select elements
const element = document.getElementById('myId');
const elements = document.querySelectorAll('.myClass');
const first = document.querySelector('div > p');

// Modify content
element.textContent = 'New text';
element.innerHTML = '<strong>Bold text</strong>';

// Modify attributes
element.setAttribute('data-id', '123');
element.classList.add('active');
element.classList.remove('hidden');
element.classList.toggle('visible');

// Modify styles
element.style.color = 'red';
element.style.backgroundColor = 'blue';

// Create & append elements
const newDiv = document.createElement('div');
newDiv.textContent = 'New element';
document.body.appendChild(newDiv);

// Event listeners
element.addEventListener('click', () => {
    console.log('Clicked!');
});

element.addEventListener('change', (event) => {
    console.log(event.target.value);
});
